import React from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import useProductList from './hooks/useProductList';
import ErrorComponent from './components/ErrorComponent';
import ErrorBoundary from './components/ErrorBoundary';

function App() {
  const {
    loading,
    error,
    nameFilter,
    maxPriceFilter,
    sortOrder,
    filteredAndSortedProducts,
    deletingProductId,
    setNameFilter,
    handleMaxPriceChange,
    setSortOrder,
    handleProductAdded,
    handleDeleteProduct,
    handleUpdateQuantity, // Assurez-vous que cette fonction est importée depuis useProductList
    fetchProducts
  } = useProductList();

  return (
    <div className="App">
      <h1>Gestion des Produits</h1>
      <ErrorBoundary>
        <ProductForm onProductAdded={handleProductAdded} />
        <ProductList 
          products={filteredAndSortedProducts}
          loading={loading}
          error={error}
          nameFilter={nameFilter}
          maxPriceFilter={maxPriceFilter}
          sortOrder={sortOrder}
          deletingProductId={deletingProductId}
          onDeleteProduct={handleDeleteProduct}
          onNameFilterChange={setNameFilter}
          onMaxPriceFilterChange={handleMaxPriceChange}
          onSortOrderChange={setSortOrder}
          onUpdateQuantity={handleUpdateQuantity} // Ajoutez cette ligne
          onRefresh={fetchProducts}
        />
      </ErrorBoundary>
      {error && <ErrorComponent data={error} />}
    </div>
  );
}

export default App;
