import React from 'react';
import PropTypes from 'prop-types';
import '../styles/ProductList.css';

const FilterBar = ({ nameFilter, maxPriceFilter, sortOrder, onNameFilterChange, onMaxPriceFilterChange, onSortOrderChange, onRefresh }) => (
  <div className="filters">
    <input
      type="text"
      placeholder="Filtrer par nom"
      value={nameFilter}
      onChange={(e) => onNameFilterChange(e.target.value)}
    />
    <input
      type="number"
      placeholder="Prix max"
      value={maxPriceFilter}
      onChange={onMaxPriceFilterChange}
    />
    <select value={sortOrder} onChange={(e) => onSortOrderChange(e.target.value)}>
      <option value="asc">Prix croissant</option>
      <option value="desc">Prix décroissant</option>
    </select>
    <button onClick={onRefresh}>Rafraîchir</button>
  </div>
);

const ProductItem = ({ product, onDeleteProduct, onUpdateQuantity, deletingProductId }) => (
  <li>
    <span>
      {product.name} - Prix: {product.price}€ - Stock: 
      <input
        type="number"
        value={product.stock}
        onChange={(e) => onUpdateQuantity(product._id, parseInt(e.target.value, 10))}
        min="0"
      />
      - Prix de revient: {product.costPrice}€
    </span>
    <button onClick={() => onDeleteProduct(product._id)} disabled={deletingProductId === product._id}>
      {deletingProductId === product._id ? 'Suppression...' : 'Supprimer'}
    </button>
  </li>
);

const ProductList = ({
  products,
  loading,
  error,
  nameFilter,
  maxPriceFilter,
  sortOrder,
  deletingProductId,
  onDeleteProduct,
  onNameFilterChange,
  onMaxPriceFilterChange,
  onSortOrderChange,
  onUpdateQuantity,
  onRefresh
}) => {
  return (
    <div className="product-list">
      <h2>Liste des Produits</h2>
      {loading && <p>Chargement des produits...</p>}
      {error && <p className="error-message">{error}</p>}
      
      <FilterBar
        nameFilter={nameFilter}
        maxPriceFilter={maxPriceFilter}
        sortOrder={sortOrder}
        onNameFilterChange={onNameFilterChange}
        onMaxPriceFilterChange={onMaxPriceFilterChange}
        onSortOrderChange={onSortOrderChange}
        onRefresh={onRefresh}
      />
      
      {Array.isArray(products) && products.length > 0 ? (
        <ul>
          {products.map(product => (
            <ProductItem
              key={product._id}
              product={product}
              onDeleteProduct={onDeleteProduct}
              onUpdateQuantity={onUpdateQuantity}
              deletingProductId={deletingProductId}
            />
          ))}
        </ul>
      ) : (
        <p>Aucun produit trouvé.</p>
      )}
    </div>
  );
};

ProductList.propTypes = {
  products: PropTypes.array.isRequired,
  loading: PropTypes.bool.isRequired,
  error: PropTypes.string,
  nameFilter: PropTypes.string.isRequired,
  maxPriceFilter: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
  sortOrder: PropTypes.string.isRequired,
  deletingProductId: PropTypes.string,
  onDeleteProduct: PropTypes.func.isRequired,
  onNameFilterChange: PropTypes.func.isRequired,
  onMaxPriceFilterChange: PropTypes.func.isRequired,
  onSortOrderChange: PropTypes.func.isRequired,
  onUpdateQuantity: PropTypes.func.isRequired,
  onRefresh: PropTypes.func.isRequired
};

export default ProductList;
