import React, { useState, useCallback } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import '../styles/ProductForm.css';

function ProductForm({ onProductAdded }) {
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    costPrice: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = useCallback((e) => {
    const { id, value } = e.target;
    setFormData(prevData => ({ ...prevData, [id]: value }));
  }, []);

  const resetForm = useCallback(() => {
    setFormData({ name: '', price: '', costPrice: '' });
    setError('');
    setSuccess(false);
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setIsLoading(true);

    const { name, price, costPrice } = formData;
    const trimmedName = name.trim();
    const parsedPrice = parseFloat(price);
    const parsedCostPrice = parseFloat(costPrice);

    if (!trimmedName || isNaN(parsedPrice) || isNaN(parsedCostPrice) || parsedPrice < 0 || parsedCostPrice < 0) {
      setError('Veuillez remplir tous les champs correctement. Le prix et le prix de revient doivent être des nombres positifs.');
      setIsLoading(false);
      return;
    }

    const productData = {
      name: trimmedName,
      price: parsedPrice,
      costPrice: parsedCostPrice,
    };

    try {
      const response = await axios.post('http://localhost:50687/products', productData);
      onProductAdded(response.data);
      setSuccess(true);
      resetForm();
    } catch (error) {
      console.error('Erreur lors de l\'ajout du produit:', error.response?.data || error.message);
      setError(`Erreur lors de l'ajout du produit: ${error.response?.data?.message || error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [formData, onProductAdded, resetForm]);

  return (
    <div className="product-form-container">
      <form className="product-form" onSubmit={handleSubmit}>
        <h2>Ajouter un Produit</h2>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">Le produit a été ajouté avec succès !</p>}
        
        <div className="form-group">
          <label htmlFor="name">Nom du produit</label>
          <input
            id="name"
            type="text"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Prix (€)</label>
          <input
            id="price"
            type="number"
            value={formData.price}
            onChange={handleChange}
            required
            min="0"
            step="0.01"
          />
        </div>

        <div className="form-group">
          <label htmlFor="costPrice">Prix de revient (€)</label>
          <input
            id="costPrice"
            type="number"
            value={formData.costPrice}
            onChange={handleChange}
            required
            min="0"
            step="0.01"
          />
        </div>

        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Ajout en cours...' : 'Ajouter le produit'}
        </button>
      </form>
    </div>
  );
}

ProductForm.propTypes = {
  onProductAdded: PropTypes.func.isRequired,
};

export default ProductForm;
