import { useState, useEffect } from 'react';
import axios from 'axios';

const useProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [nameFilter, setNameFilter] = useState('');
  const [maxPriceFilter, setMaxPriceFilter] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const [deletingProductId, setDeletingProductId] = useState(null);

  const fetchProducts = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('http://localhost:50787/products');
      setProducts(response.data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleProductAdded = (newProduct) => {
    setProducts([...products, newProduct]);
  };

  const handleDeleteProduct = async (id) => {
    setDeletingProductId(id);
    try {
      await axios.delete(`http://localhost:50787/products/${id}`);
      setProducts(products.filter(product => product.id !== id));
    } catch (err) {
      setError(err.message);
    } finally {
      setDeletingProductId(null);
    }
  };

  const handleUpdateQuantity = async (id, newQuantity) => {
    try {
      await axios.patch(`http://localhost:50787/products/${id}`, { stock: newQuantity });
      fetchProducts(); // Rafraîchir la liste des produits après la mise à jour
    } catch (error) {
      setError('Erreur lors de la mise à jour de la quantité');
      console.error('Erreur:', error);
    }
  };

  const handleMaxPriceChange = (e) => {
    setMaxPriceFilter(e.target.value);
  };

  const filteredAndSortedProducts = products
    .filter(product => product.name.toLowerCase().includes(nameFilter.toLowerCase()) && 
                       (maxPriceFilter === '' || product.price <= parseFloat(maxPriceFilter)))
    .sort((a, b) => (sortOrder === 'asc' ? a.price - b.price : b.price - a.price));

  return {
    loading,
    error,
    nameFilter,
    maxPriceFilter,
    sortOrder,
    filteredAndSortedProducts,
    deletingProductId,
    setNameFilter,
    handleMaxPriceChange,
    setSortOrder,
    handleProductAdded,
    handleDeleteProduct,
    handleUpdateQuantity,
    fetchProducts
  };
};

export default useProductList;
