import React, { useState, useCallback } from 'react';
import { 
  Button, TextField, Paper, Typography, Grid, 
  CircularProgress, Box 
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import brain from './brain';

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(3),
  },
  input: {
    marginBottom: theme.spacing(2),
  },
  result: {
    marginTop: theme.spacing(2),
  },
}));

function BrainJSComponent() {
  const classes = useStyles();
  const [input, setInput] = useState([0, 0]);
  const [output, setOutput] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleInputChange = (index) => (event) => {
    const newInput = [...input];
    newInput[index] = Number(event.target.value);
    setInput(newInput);
  };

  const predict = useCallback(() => {
    setLoading(true);
    setTimeout(() => {
      const result = brain.predictWithDebug(input);
      setOutput(result);
      setLoading(false);
    }, 500); // Simulating network delay
  }, [input]);

  return (
    <Paper className={classes.root}>
      <Typography variant="h5" gutterBottom>
        Brain.js Prediction
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <TextField
            className={classes.input}
            label="Input 1"
            type="number"
            value={input[0]}
            onChange={handleInputChange(0)}
            fullWidth
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            className={classes.input}
            label="Input 2"
            type="number"
            value={input[1]}
            onChange={handleInputChange(1)}
            fullWidth
          />
        </Grid>
      </Grid>
      <Button 
        variant="contained" 
        color="primary" 
        onClick={predict}
        disabled={loading}
      >
        {loading ? <CircularProgress size={24} /> : 'Predict'}
      </Button>
      {output !== null && (
        <Box className={classes.result}>
          <Typography variant="h6">Result:</Typography>
          <Typography>{output[0]}</Typography>
        </Box>
      )}
    </Paper>
  );
}

export default BrainJSComponent;
