import React, { useState, useCallback } from 'react';
import axios from 'axios';
import '../styles/ProductForm.css';

function ProductForm({ onProductAdded }) {
  const initialState = { name: '', price: '', stock: '', costPrice: '' };
  const [formData, setFormData] = useState(initialState);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = useCallback((e) => {
    const { id, value } = e.target;
    setFormData(prevData => ({ ...prevData, [id]: value }));
  }, []);

  const resetForm = useCallback(() => {
    setFormData(initialState);
    setError('');
    setSuccess(false);
  }, []);

  const validateForm = useCallback(() => {
    const { name, price, stock, costPrice } = formData;
    if (!name.trim() || isNaN(price) || isNaN(stock) || isNaN(costPrice) || 
        parseFloat(price) < 0 || parseInt(stock) < 0 || parseFloat(costPrice) < 0) {
      setError('Veuillez remplir tous les champs correctement. Le prix, le stock et le prix de revient doivent être des nombres positifs.');
      return false;
    }
    return true;
  }, [formData]);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setIsLoading(true);

    if (!validateForm()) {
      setIsLoading(false);
      return;
    }

    const productData = {
      name: formData.name.trim(),
      price: parseFloat(formData.price),
      stock: parseInt(formData.stock, 10),
      costPrice: parseFloat(formData.costPrice),
    };

    try {
      const response = await axios.post('http://localhost:50687/products', productData);
      onProductAdded(response.data);
      setSuccess(true);
      resetForm();
    } catch (error) {
      console.error('Erreur lors de l\'ajout du produit:', error.response?.data || error.message);
      setError(`Erreur lors de l'ajout du produit: ${error.response?.data?.message || error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [formData, onProductAdded, resetForm, validateForm]);

  return (
    <form className="product-form" onSubmit={handleSubmit}>
      <h2>Ajouter un Produit</h2>
      {error && <p className="error-message" role="alert">{error}</p>}
      {success && <p className="success-message" role="status">Le produit a été ajouté avec succès !</p>}
      {Object.entries(formData).map(([key, value]) => (
        <div className="form-group" key={key}>
          <label htmlFor={key}>{key === 'name' ? 'Nom' : key === 'price' ? 'Prix (€)' : key === 'stock' ? 'Stock' : 'Prix de revient (€)'}</label>
          <input
            id={key}
            type={key === 'name' ? 'text' : 'number'}
            value={value}
            onChange={handleChange}
            min={key !== 'name' ? "0" : undefined}
            step={key === 'price' || key === 'costPrice' ? "0.01" : undefined}
            required
            className={value && (key === 'name' || (!isNaN(parseFloat(value)) && parseFloat(value) >= 0)) ? '' : 'invalid'}
            aria-invalid={value && (key === 'name' || (!isNaN(parseFloat(value)) && parseFloat(value) >= 0)) ? 'false' : 'true'}
          />
        </div>
      ))}
      <button type="submit" disabled={isLoading}>
        {isLoading ? 'Ajout en cours...' : 'Ajouter'}
      </button>
    </form>
  );
}

export default ProductForm;
