import { useState, useEffect, useMemo, useCallback } from 'react';
import axios from 'axios';
import { API_URL } from '../config';

function useProductList() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [nameFilter, setNameFilter] = useState('');
  const [maxPriceFilter, setMaxPriceFilter] = useState('');
  const [sortOrder, setSortOrder] = useState('');
  const [deletingProductId, setDeletingProductId] = useState(null);

  function ProductList() {
    const { loading, error, filteredAndSortedProducts } = useProductList();
  
    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>;
  
    return (
      <div>
        {filteredAndSortedProducts.map(product => (
          <div key={product._id}>{product.name}</div>
        ))}
      </div>
    );
  }
  

  const fetchProducts = useCallback(async () => {
    try {
      setLoading(true);
      console.log('Fetching products...');
      const response = await axios.get(`${API_URL}/products`);
      console.log('Products fetched:', response.data);
      setProducts(response.data);
      setError(null);
    } catch (error) {
      console.error('Error fetching products:', error);
      setError(error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    console.log('useEffect triggered, calling fetchProducts');
    fetchProducts();
  }, [fetchProducts]);

  const handleProductAdded = useCallback((newProduct) => {
    console.log('Adding new product:', newProduct);
    setProducts(prevProducts => [...prevProducts, newProduct]);
  }, []);

  const handleDeleteProduct = async (productId) => {
    try {
      console.log(`Attempting to delete product with ID: ${productId}`);
      setDeletingProductId(productId);
      await axios.delete(`${API_URL}/products/${productId}`);
      console.log(`Product with ID: ${productId} successfully deleted`);
      setProducts(prevProducts => prevProducts.filter(product => product._id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error.response?.data || error.message);
      setError(`Erreur lors de la suppression : ${error.response?.data?.message || error.message}`);
    } finally {
      setDeletingProductId(null);
    }
  };

  const handleMaxPriceChange = (e) => {
    const value = e.target.value;
    if (value === '' || (!isNaN(parseFloat(value)) && isFinite(value))) {
      console.log(`Setting max price filter to: ${value}`);
      setMaxPriceFilter(value);
    }
  };

  const filteredAndSortedProducts = useMemo(() => {
    console.log('Recalculating filtered and sorted products');
    return products
      .filter(product => product.name.toLowerCase().includes(nameFilter.toLowerCase()))
      .filter(product => maxPriceFilter === '' || product.price <= parseFloat(maxPriceFilter))
      .sort((a, b) => {
        if (sortOrder === 'asc') {
          return a.price - b.price;
        } else if (sortOrder === 'desc') {
          return b.price - a.price;
        }
        return 0;
      });
  }, [products, nameFilter, maxPriceFilter, sortOrder]);

  console.log('Current state:', {
    productsCount: products.length,
    loading,
    error,
    nameFilter,
    maxPriceFilter,
    sortOrder,
    deletingProductId
  });

  return {
    loading,
    error,
    nameFilter,
    maxPriceFilter,
    sortOrder,
    filteredAndSortedProducts,
    deletingProductId,
    setNameFilter,
    handleMaxPriceChange,
    setSortOrder,
    handleProductAdded,
    handleDeleteProduct,
    fetchProducts
  };
}

export default useProductList;
