import React, { useState, useCallback } from 'react';
import axios from 'axios';
import '../styles/ProductForm.css';

function ProductForm({ onProductAdded, onStockUpdated }) {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState('');
  const [costPrice, setCostPrice] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [stockQuantity, setStockQuantity] = useState(0);
  const [products, setProducts] = useState([]);



  const resetForm = useCallback(() => {
    setName('');
    setPrice('');
    setStock('');
    setCostPrice('');
    setError('');
    setSuccess(false);
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setIsLoading(true);

    const trimmedName = name.trim();
    const parsedPrice = parseFloat(price);
    const parsedStock = parseInt(stock, 10);
    const parsedCostPrice = parseFloat(costPrice);

    if (!trimmedName || isNaN(parsedPrice) || isNaN(parsedStock) || parsedPrice < 0 || parsedStock < 0 || isNaN(parsedCostPrice) || parsedCostPrice < 0) {
      setError('Veuillez remplir tous les champs correctement. Le prix, le stock et le prix de revient doivent être des nombres positifs.');
      setIsLoading(false);
      return;
    }

    const productData = {
      name: trimmedName,
      price: parsedPrice,
      stock: parsedStock,
      costPrice: parsedCostPrice,
    };

    console.log('URL de l\'API:', 'http://localhost:5000/products');
    console.log('Données du produit envoyées:', productData);

    try {
      const response = await axios.post('http://localhost:50687/products', productData);

      console.log('Réponse du serveur:', response.data);
      onProductAdded(response.data);
      setSuccess(true);
      resetForm();
    } catch (error) {
      console.error('Erreur lors de l\'ajout du produit:', error.response?.data || error.message);
      setError(`Erreur lors de l'ajout du produit: ${error.response?.data?.message || error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [name, price, stock, costPrice, onProductAdded, resetForm]);

  const handleStockUpdate = useCallback(async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setIsLoading(true);

    const productId = e.target.dataset.productId;
    const newStockQuantity = parseInt(e.target.value, 10);

    if (isNaN(newStockQuantity) || newStockQuantity < 0) {
      setError('Veuillez entrer une quantité de stock valide.');
      setIsLoading(false);
      return;
    }

    try {
      const response = await axios.patch(`http://localhost:50687/products/${productId}/stock`, { stock: newStockQuantity });

      console.log('Réponse du serveur:', response.data);
      onStockUpdated(response.data);
      setSuccess(true);
    } catch (error) {
      console.error('Erreur lors de la mise à jour du stock:', error.response?.data || error.message);
      setError(`Erreur lors de la mise à jour du stock: ${error.response?.data?.message || error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [onStockUpdated]);

  return (
    <form className="product-form" onSubmit={handleSubmit}>
      <h2>Ajouter un Produit</h2>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">Le produit a été ajouté avec succès !</p>}
      <div className="form-group">
        <label htmlFor="name">Nom</label>
        <input
          id="name"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          className={name.trim() ? '' : 'invalid'}
        />
      </div>
      <div className="form-group">
        <label htmlFor="price">Prix (€)</label>
        <input
          id="price"
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          min="0"
          step="0.01"
          required
          className={!isNaN(parseFloat(price)) && parseFloat(price) >= 0 ? '' : 'invalid'}
        />
      </div>
      <div className="form-group">
        <label htmlFor="stock">Stock</label>
        <input
          id="stock"
          type="number"
          value={stock}
          onChange={(e) => setStock(e.target.value)}
          min="0"
          required
          className={!isNaN(parseInt(stock, 10)) && parseInt(stock, 10) >= 0 ? '' : 'invalid'}
        />
      </div>
      <div className="form-group">
        <label htmlFor="costPrice">Prix de revient (€)</label>
        <input
          id="costPrice"
          type="number"
          value={costPrice}
          onChange={(e) => setCostPrice(e.target.value)}
          min="0"
          step="0.01"
          required
          className={!isNaN(parseFloat(costPrice)) && parseFloat(costPrice) >= 0 ? '' : 'invalid'}
        />
      </div>
      <button type="submit" disabled={isLoading}>
        {isLoading ? 'Ajout en cours...' : 'Ajouter'}
      </button>

      <h2>Consulter et modifier le stock</h2>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Stock</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>
                <input
                  type="number"
                  value={stockQuantity}
                  onChange={(e) => setStockQuantity(e.target.value)}
                  min="0"
                  required
                  className={!isNaN(parseInt(stockQuantity, 10)) && parseInt(stockQuantity, 10) >= 0 ? '' : 'invalid'}
                />
              </td>
              <td>
                <button
                  type="button"
                  onClick={handleStockUpdate}
                  data-product-id={product.id}
                >
                  Mettre à jour le stock
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </form>
  );
}

export default ProductForm;
