import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { 
  Container, Typography, Grid, Card, CardContent, 
  Button, TextField, Snackbar, CircularProgress
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import MuiAlert from '@material-ui/lab/Alert';

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardContent: {
    flexGrow: 1,
  },
  quantityField: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
  sellButton: {
    marginTop: 'auto',
  },
}));

function SalesInterface() {
  const classes = useStyles();
  const [products, setProducts] = useState([]);
  const [sales, setSales] = useState({});
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchProducts = useCallback(async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:50787/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
      setMessage('Erreur lors de la récupération des produits');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  const handleSaleChange = (productId, quantity) => {
    setSales({ ...sales, [productId]: quantity });
  };

  const handleSale = async (productId) => {
    try {
      const quantity = parseInt(sales[productId]) || 0;
      if (quantity <= 0) {
        setMessage('La quantité doit être supérieure à 0');
        return;
      }
      const product = products.find(p => p._id === productId);
      if (quantity > product.stock) {
        setMessage('La quantité vendue ne peut pas dépasser le stock disponible');
        return;
      }
      await axios.patch(`http://localhost:50787/products/${productId}`, { 
        stock: product.stock - quantity 
      });
      setMessage('Vente enregistrée avec succès');
      setSales({ ...sales, [productId]: 0 });
      fetchProducts(); // Rafraîchir la liste des produits après la vente
    } catch (error) {
      console.error('Error processing sale:', error);
      setMessage('Erreur lors de l\'enregistrement de la vente');
    }
  };

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom>Interface de Vente</Typography>
      <Grid container spacing={3}>
        {products.map((product) => (
          <Grid item xs={12} sm={6} md={4} key={product._id}>
            <Card className={classes.card}>
              <CardContent className={classes.cardContent}>
                <Typography variant="h6">{product.name}</Typography>
                <Typography>Prix: {product.price}€</Typography>
                <Typography>Stock: {product.stock}</Typography>
                <TextField
                  className={classes.quantityField}
                  type="number"
                  label="Quantité à vendre"
                  value={sales[product._id] || ''}
                  onChange={(e) => handleSaleChange(product._id, e.target.value)}
                  inputProps={{ min: 0, max: product.stock }}
                  fullWidth
                />
                <Button 
                  className={classes.sellButton}
                  variant="contained" 
                  color="primary" 
                  onClick={() => handleSale(product._id)}
                  disabled={!sales[product._id] || sales[product._id] <= 0 || sales[product._id] > product.stock}
                  fullWidth
                >
                  Vendre
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Snackbar open={!!message} autoHideDuration={6000} onClose={() => setMessage('')}>
        <Alert onClose={() => setMessage('')} severity="info">
          {message}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default SalesInterface;
