import React from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import useProductList from './hooks/useProductList';

require('dotenv').config();

// Exemple d'utilisation des variables d'environnement
const dbHost = process.env.DB_HOST;
const dbPort = process.env.DB_PORT;
const dbName = process.env.DB_NAME;
const dbUser = process.env.DB_USER;
// const dbPass = process.env.REACT_APP_DB_PASS; // Commentez ou supprimez cette ligne si elle n'est pas utilisée
const apiKey = process.env.API_KEY;

console.log(`Connecting to database at ${dbHost}:${dbPort}/${dbName} with user ${dbUser}`);
console.log(`API Key: ${apiKey}`);

const HeavyComponent = () => {
  return (
    <div>
      <h2>Heavy Component</h2>
      <p>This is a heavy component.</p>
    </div>
  );
};

function App() {
  const {
    loading,
    error,
    nameFilter,
    maxPriceFilter,
    sortOrder,
    filteredAndSortedProducts,
    deletingProductId,
    setNameFilter,
    handleMaxPriceChange,
    setSortOrder,
    handleProductAdded,
    handleDeleteProduct,
    fetchProducts
  } = useProductList();

  return (
    <div className="App">
      <h1>Gestion des Produits</h1>
      <ProductForm onProductAdded={handleProductAdded} />
      <ProductList 
        products={filteredAndSortedProducts}
        loading={loading}
        error={error}
        nameFilter={nameFilter}
        maxPriceFilter={maxPriceFilter}
        sortOrder={sortOrder}
        deletingProductId={deletingProductId}
        onDeleteProduct={handleDeleteProduct}
        onNameFilterChange={setNameFilter}
        onMaxPriceFilterChange={handleMaxPriceChange}
        onSortOrderChange={setSortOrder}
        onRefresh={fetchProducts}
      />
    </div>
  );
}

export default App;
