import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import '../styles/ProductForm.css';

function ProductEditForm({ productId, onProductUpdated }) {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [costPrice, setCostPrice] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Fonction pour récupérer les détails du produit à modifier
  const fetchProductDetails = useCallback(async () => {
    if (!productId) {
      setError('ID du produit non défini.');
      return;
    }

    try {
      const response = await axios.get(`http://localhost:50687/products/${productId}`);
      const product = response.data;
      setName(product.name);
      setPrice(product.price);
      setCostPrice(product.costPrice);
    } catch (error) {
      console.error('Erreur lors de la récupération du produit:', error);
      setError('Erreur lors de la récupération des détails du produit.');
    }
  }, [productId]);

  useEffect(() => {
    fetchProductDetails();
  }, [fetchProductDetails]);

  const resetForm = useCallback(() => {
    setName('');
    setPrice('');
    setCostPrice('');
    setError('');
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    const trimmedName = name.trim();
    const parsedPrice = parseFloat(price);
    const parsedCostPrice = parseFloat(costPrice);

    // Validation des champs
    if (!trimmedName || isNaN(parsedPrice) || isNaN(parsedCostPrice) || parsedPrice < 0 || parsedCostPrice < 0) {
      setError('Veuillez remplir tous les champs correctement. Le prix et le prix de revient doivent être des nombres positifs.');
      setIsLoading(false);
      return;
    }

    const productData = {
      name: trimmedName,
      price: parsedPrice,
      costPrice: parsedCostPrice,
    };

    console.log('Données du produit modifiées:', productData);

    try {
      const response = await axios.put(`http://localhost:50687/products/${productId}`, productData);
      console.log('Réponse du serveur:', response.data);
      onProductUpdated(response.data);
      resetForm();
    } catch (error) {
      console.error('Erreur lors de la modification du produit:', error.response?.data || error.message);
      setError(`Erreur lors de la modification du produit: ${error.response?.data?.message || error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [name, price, costPrice, productId, onProductUpdated, resetForm]);

  return (
    <form className="product-form" onSubmit={handleSubmit}>
      <h2>Modifier un Produit</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="form-group">
        <label htmlFor="name">Nom</label>
        <input
          id="name"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          className={name.trim() ? '' : 'invalid'}
        />
      </div>
      <div className="form-group">
        <label htmlFor="price">Prix (€)</label>
        <input
          id="price"
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          min="0"
          step="0.01"
          required
          className={!isNaN(parseFloat(price)) && parseFloat(price) >= 0 ? '' : 'invalid'}
        />
      </div>
      <div className="form-group">
        <label htmlFor="cost-price">Prix de revient (€)</label>
        <input
          id="cost-price"
          type="number"
          value={costPrice}
          onChange={(e) => setCostPrice(e.target.value)}
          min="0"
          required
          className={!isNaN(parseFloat(costPrice)) && parseFloat(costPrice) >= 0 ? '' : 'invalid'}
        />
      </div>
      <button type="submit" disabled={isLoading}>
        {isLoading ? 'Modification en cours...' : 'Modifier'}
      </button>
    </form>
  );
}

export default ProductEditForm;
