import React, { useState, useCallback, useEffect } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import '../styles/ProductForm.css';

function ProductForm({ onProductAdded }) {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    stock: '',
    costPrice: ''
  });
  const [formError, setFormError] = useState('');
  const [stockError, setStockError] = useState('');
  const [success, setSuccess] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:50687/products');
      setProducts(response.data);
    } catch (error) {
      setStockError('Erreur lors du chargement des produits');
      console.error('Erreur:', error);
    }
  }, []);

  const handleChange = useCallback((e) => {
    const { id, value } = e.target;
    setFormData(prevData => ({ ...prevData, [id]: value }));
  }, []);

  const resetForm = useCallback(() => {
    setFormData({ name: '', price: '', stock: '', costPrice: '' });
    setFormError('');
    setSuccess(false);
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    setFormError('');
    setSuccess(false);
    setIsLoading(true);

    const { name, price, stock, costPrice } = formData;
    const trimmedName = name.trim();
    const parsedPrice = parseFloat(price);
    const parsedStock = parseInt(stock, 10);
    const parsedCostPrice = parseFloat(costPrice);

    if (!trimmedName || isNaN(parsedPrice) || isNaN(parsedStock) || parsedPrice < 0 || parsedStock < 0 || isNaN(parsedCostPrice) || parsedCostPrice < 0) {
      setFormError('Veuillez remplir tous les champs correctement. Le prix, le stock et le prix de revient doivent être des nombres positifs.');
      setIsLoading(false);
      return;
    }

    const productData = {
      name: trimmedName,
      price: parsedPrice,
      stock: parsedStock,
      costPrice: parsedCostPrice,
    };

    try {
      const response = await axios.post('http://localhost:50687/products', productData);
      onProductAdded(response.data);
      setSuccess(true);
      resetForm();
      fetchProducts(); // Rafraîchir la liste des produits
    } catch (error) {
      console.error('Erreur lors de l\'ajout du produit:', error.response?.data || error.message);
      setFormError(`Erreur lors de l'ajout du produit: ${error.response?.data?.message || error.message}`);
    } finally {
      setIsLoading(false);
    }
  }, [formData, onProductAdded, resetForm, fetchProducts]);

  const handleUpdateQuantity = useCallback(async (id, newQuantity) => {
    if (!id || isNaN(newQuantity) || newQuantity < 0) {
      setStockError('ID de produit invalide ou quantité incorrecte');
      return;
    }

    try {
      await axios.patch(`http://localhost:50687/products/${id}`, { stock: newQuantity });
      fetchProducts();
    } catch (error) {
      setStockError('Erreur lors de la mise à jour de la quantité');
      console.error('Erreur:', error);
    }
  }, [fetchProducts]);

  return (
    <div className="product-manager">
      <form className="product-form" onSubmit={handleSubmit}>
        <h2>Ajouter un Produit</h2>
        {formError && <p className="error-message">{formError}</p>}
        {success && <p className="success-message">Le produit a été ajouté avec succès !</p>}
        {['name', 'price', 'stock', 'costPrice'].map((field) => (
          <div className="form-group" key={field}>
            <label htmlFor={field}>
              {field === 'name' ? 'Nom' : 
               field === 'price' ? 'Prix (€)' : 
               field === 'stock' ? 'Stock' : 'Prix de revient (€)'}
            </label>
            <input
              id={field}
              type={field === 'name' ? 'text' : 'number'}
              value={formData[field]}
              onChange={handleChange}
              required
              min={field !== 'name' ? "0" : undefined}
              step={field === 'price' || field === 'costPrice' ? "0.01" : undefined}
              className={formData[field] ? '' : 'invalid'}
            />
          </div>
        ))}
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Ajout en cours...' : 'Ajouter'}
        </button>
      </form>

      <div className="stock-manager">
        <h2>Gestion du Stock</h2>
        {stockError && <p className="error-message">{stockError}</p>}
        <table className="product-table">
          <thead>
            <tr>
              <th>Nom du Produit</th>
              <th>Prix</th>
              <th>Quantité en Stock</th>
              <th>Prix de Revient</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.id}>
                <td>{product.name}</td>
                <td>{product.price} €</td>
                <td>
                  <input
                    type="number"
                    value={product.stock}
                    onChange={(e) => handleUpdateQuantity(product.id, parseInt(e.target.value, 10))}
                    min="0"
                  />
                </td>
                <td>{product.costPrice} €</td>
                <td>
                  <button onClick={() => handleUpdateQuantity(product.id, product.stock)}>
                    Mettre à jour
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

ProductForm.propTypes = {
  onProductAdded: PropTypes.func.isRequired,
};

export default ProductForm;
