import React from 'react';
import useProductList from '../hooks/useProductList';
import ProductList from './ProductList';

const SalesInterface = () => {
  const {
    loading,
    error,
    filteredAndSortedProducts,
    handleDeleteProduct, // Assurez-vous que cette fonction est bien récupérée du hook
    // ... autres propriétés et fonctions nécessaires
  } = useProductList();

  return (
    <div>
      <h1>Interface de Vente</h1>
      <ProductList 
        products={filteredAndSortedProducts}
        loading={loading}
        error={error}
        onDeleteProduct={handleDeleteProduct} // Passez la fonction ici
        // ... autres props nécessaires
      />
    </div>
  );
};

export default SalesInterface;
